from axiestudio.custom import Component
from axiestudio.io import MultilineInput, Output
from axiestudio.schema.message import Message


class SubQuestionExtractorComponent(Component):
    display_name: str = "Sub-question Extractor"
    description: str = "Extract sub-questions from a multiline string and output each one separately."
    icon = "questions"
    name = "SubQuestionExtractor"

    inputs = [
        MultilineInput(
            name="questions",
            display_name="Questions",
            info="A multiline string containing sub-questions.",
        ),
    ]

    # Define separate outputs for each sub-question
    outputs = [
        Output(display_name="Sub-question 1", name="sub_question_1", method="get_sub_question_1"),
        Output(display_name="Sub-question 2", name="sub_question_2", method="get_sub_question_2"),
        Output(display_name="Sub-question 3", name="sub_question_3", method="get_sub_question_3"),
    ]

    def extract_sub_questions(self):
        # Strip input and split by new lines, filtering out empty lines
        questions = [q.strip() for q in self.questions.strip().split('\n') if q.strip()]
        
        extracted = {}
        
        # Dynamically extract sub-questions
        for i, question in enumerate(questions, start=1):
            extracted[f"Sub-question {i}"] = question

        return extracted

    # Define separate methods to return each sub-question
    def get_sub_question_1(self) -> Message:
        extracted_questions = self.extract_sub_questions()
        return Message(text=extracted_questions.get("Sub-question 1", "No Sub-question 1 found"))

    def get_sub_question_2(self) -> Message:
        extracted_questions = self.extract_sub_questions()
        return Message(text=extracted_questions.get("Sub-question 2", "No Sub-question 2 found"))

    def get_sub_question_3(self) -> Message:
        extracted_questions = self.extract_sub_questions()
        return Message(text=extracted_questions.get("Sub-question 3", "No Sub-question 3 found"))

