from typing import List
from axiestudio import CustomComponent
from axiestudio.field_typing import Document

class RecursiveCharacterSplitterWithMetadata(CustomComponent):
    display_name: str = "RecursiveCharacterSplitterWithMetadata"
    description: str = "Split documents into chunks with overlapping content."

    field_config = {
        "documents": {
            "display_name": "Documents",
            "field_type": "List[Document]",
            "required": True,
        },
        "chunk_size": {
            "display_name": "Chunk Size",
            "field_type": "int",
            "required": True,
            "value": 1000,
        },
        "chunk_overlap": {
            "display_name": "Chunk Overlap",
            "field_type": "int",
            "required": True,
            "value": 200,
        },
        "separator": {
            "display_name": "Separator",
            "field_type": "str",
            "required": True,
            "value": "\n",
        }
    }

    def build_config(self):
        return self.field_config

    def _get_field_order(self):
        return ["documents", "chunk_size", "chunk_overlap", "separator"]

    def build(self, documents: List[Document], chunk_size: int, chunk_overlap: int, separator: str) -> List[Document]:
        def split(document: Document) -> List[Document]:
            chunks = []
            content = document.page_content
            metadata = document.metadata
            start = 0
            while start < len(content):
                end = start + chunk_size
                chunk = content[start:end]
                if end < len(content):
                    chunk = chunk.rsplit(separator, 1)[0]
                chunks.append(Document(page_content=chunk, metadata=metadata))
                start += chunk_size - chunk_overlap
            return chunks

        results = []
        for document in documents:
            results.extend(split(document))
        return results

    def input(self, documents: List[Document]) -> List[Document]:
        return self.build(
            documents,
            self.field_config['chunk_size']['value'],
            self.field_config['chunk_overlap']['value'],
            self.field_config['separator']['value']
        )

from axiestudio import CustomComponent
from axiestudio.field_typing import BaseLLM, PromptTemplate, VectorStore, Chain
from langchain.chains import RetrievalQA
from langchain.vectorstores import Weaviate

class WeaviateComponent(CustomComponent):
    documentation: str = "http://docs.axiestudio.org/components/custom"

    def build(self, llm: BaseLLM, prompt: PromptTemplate, vector_store: VectorStore) -> Chain:
        # Ensure the vector_store is an instance of Weaviate
        if not isinstance(vector_store, Weaviate):
            raise ValueError("vector_store must be an instance of Weaviate")

        # Create the retriever with the desired number of documents
        retriever = vector_store.as_retriever(search_kwargs={"k": 10})  # Fetch top 10 relevant documents

        # Create the RetrievalQA chain with the retriever and ensure source documents are returned
        retrieval_qa = RetrievalQA.from_chain_type(
            llm=llm,
            chain_type="stuff",  # You can change this as needed
            retriever=retriever,
            return_source_documents=True
        )

        # Define the chat flow class
        class CustomConversationChain(Chain):
            @property
            def input_keys(self):
                return ["input"]

            @property
            def output_keys(self):
                return ["result", "source_documents"]

            def _call(self, inputs):
                query = inputs["input"]
                result = retrieval_qa({"query": query})
                source_documents = result.get("source_documents", [])

                if not source_documents:
                    return {
                        "result": "No relevant documents found.",
                        "source_documents": []
                    }

                unique_sources = set(doc.metadata.get('file_name', 'Unknown') for doc in source_documents)
                formatted_sources = "\n".join(f"- {source}" for source in unique_sources)
                response = result.get("result", "")

                return {
                    "result": f"{response}\n\nSources:\n{formatted_sources}",
                    "source_documents": source_documents
                }

        return CustomConversationChain()

    def build_config(self):
        return {
            "llm": {"display_name": "LLM"},
            "prompt": {"display_name": "Prompt Template"},
            "vector_store": {"display_name": "Vector Store"},
        }

from typing import List

from langchain import document_loaders
from langchain_core.documents import Document

from axiestudio import CustomComponent


class UrlLoaderComponent(CustomComponent):
    display_name: str = "Url Loader"
    description: str = "Generic Url Loader Component"

    def build_config(self):
        return {
            "web_path": {
                "display_name": "Url",
                "required": True,
            },
            "loader": {
                "display_name": "Loader",
                "is_list": True,
                "required": True,
                "options": [
                    "AZLyricsLoader",
                    "CollegeConfidentialLoader",
                    "GitbookLoader",
                    "HNLoader",
                    "IFixitLoader",
                    "IMSDbLoader",
                    "WebBaseLoader",
                ],
                "value": "WebBaseLoader",
            },
            "code": {"show": False},
        }

    def build(self, web_path: str, loader: str) -> List[Document]:
        try:
            loader_instance = getattr(document_loaders, loader)(web_path=web_path)
        except Exception as e:
            raise ValueError(f"No loader found for: {web_path}") from e
        docs = loader_instance.load()
        avg_length = sum(len(doc.page_content) for doc in docs if hasattr(doc, "page_content")) / len(docs)
        self.status = f"""{len(docs)} documents)
        \nAvg. Document Length (characters): {int(avg_length)}
        Documents: {docs[:3]}..."""
        return docs

