from axiestudio.custom import Component
from axiestudio.io import MessageInput, Output
from typing import Dict, Any
import re

class TractorInfoParser(Component):
    display_name = "Tractor Info Parser"
    description = "Extracts tractor information from the question and creates separate outputs."
    icon = "ðŸšœ"
    name = "TractorInfoParser"
    inputs = [
        MessageInput(
            name="user_question",
            display_name="User Question",
            info="Enter a question containing tractor information",
        ),
    ]
    outputs = [
        Output(name="tractor_model_code", display_name="Tractor Model Code", method="get_tractor_model_code"),
        Output(name="suspect_parameter_number", display_name="Suspect Parameter Number", method="get_suspect_parameter_number"),
        Output(name="failure_mode_identifier", display_name="Failure Mode Identifier", method="get_failure_mode_identifier"),
        Output(name="technical_bulletin_code", display_name="Technical Bulletin Code", method="get_technical_bulletin_code"),
        Output(name="service_campaign_code", display_name="Service Campaign Code", method="get_service_campaign_code"),
        Output(name="year", display_name="Year", method="get_year"),
    ]

    def __init__(self, _code=None, _user_id=None, _parameters=None, _vertex=None, _tracing_service=None, **kwargs):
        # Initialize the parent class and compile regex patterns for extraction
        super().__init__(_code=_code, _user_id=_user_id, _parameters=_parameters, _vertex=_vertex, _tracing_service=_tracing_service, **kwargs)
        self.patterns = self._initialize_patterns()
        self.compiled_patterns = {key: re.compile(pattern) for key, pattern in self.patterns.items()}

    def _initialize_patterns(self) -> Dict[str, str]:
        """Initializes regex patterns for extracting tractor information."""
        return {
            'tractor_model_code': r'\b(CP|CT|RP|RS|RT)[A-Z0-9]{2}\b',
            'suspect_parameter_number': r'\b(?:[0-9A-F]{1,6}|[0-9A-F]{1,6}\.[0-9]+)\b',
            'failure_mode_identifier': r'\b(' + '|'.join(str(i) for i in range(32) if i not in [24, 26, 27]) + r')\b',
            'technical_bulletin_code': r'\bSB_\d{4}_\d{2}\b',
            'service_campaign_code': r'\bRP_\d{4}_\d{2}\b',
            'year': r'\b(201[0-9]|202[0-5])\b'
        }

    def extract_pattern(self, text: str, pattern_key: str) -> str:
        """Extracts the first matching pattern from the text."""
        if pattern_key not in self.compiled_patterns:
            return "Pattern not found"
        match = self.compiled_patterns[pattern_key].search(text)
        return match.group() if match else "Not found"

    def get_tractor_model_code(self) -> str:
        """Extracts the tractor model code from the question text."""
        return self._extract_info('tractor_model_code')

    def get_suspect_parameter_number(self) -> str:
        """Extracts the suspect parameter number from the question text."""
        return self._extract_info('suspect_parameter_number')

    def get_failure_mode_identifier(self) -> str:
        """Extracts the failure mode identifier from the question text."""
        return self._extract_info('failure_mode_identifier')

    def get_technical_bulletin_code(self) -> str:
        """Extracts the technical bulletin code from the question text."""
        return self._extract_info('technical_bulletin_code')

    def get_service_campaign_code(self) -> str:
        """Extracts the service campaign code from the question text."""
        return self._extract_info('service_campaign_code')

    def get_year(self) -> str:
        """Extracts the year from the question text."""
        return self._extract_info('year')

    def _extract_info(self, pattern_key: str) -> str:
        """Helper method to extract information based on the provided pattern key."""
        if not hasattr(self, 'user_question') or not self.user_question.text:
            return "Question text is not available"
        return self.extract_pattern(self.user_question.text, pattern_key)

