class JSONKeyParser(CustomComponent):
    display_name: str = "JSON Key Parser"
    description: str = "Parse specific keys from a JSON object."
    output_types: list[str] = ["Record"]

    def build_config(self):
        return {
            "json_keys": {
                "display_name": "Keys",
                "info": "The keys to parse from the JSON object (comma-separated).",
                "field_type": "str",
                "required": True,
            },
            "input_value": {
                "display_name": "JSON String",
                "info": "Your JSON object.",
                "field_type": "str",
                "required": True,
            },
        }

    def build(self, input_value: Text, json_keys: str) -> Optional[Record]:
        try:
            json_obj = json.loads(input_value)
            keys = [key.strip() for key in json_keys.split(",")]
            parsed_data = {}
            for key in keys:
                if key in json_obj:
                    parsed_data[key] = json_obj[key]
                else:
                    self.status = f"Key '{key}' not found in the JSON object."
                    return None
            self.status = f"Parsed keys: {', '.join(parsed_data.keys())}"
            return Record(data=parsed_data)
        except json.JSONDecodeError:
            self.status = "Invalid JSON input."
            return None

