    loadIntakeProjects(
      limit = 10,
      page = 0,
      order = { id: "order-id", desc: true, key: "id" },
      filter = {},
      viewFilterObject = {}
    ) {
      Object.assign(filter, viewFilterObject);
      if (!order) {
        order = { id: "order-id", desc: true, key: "id" };
      }
      if (!order.key) {
        order.key = "id";
      }
      // const orderId = order['id'].split('-');
      const viewOrder = order.desc ? "DESC" : "ASC";
      const orderString = order.key + " " + viewOrder;

      return this.Project.Intake.query({
        applyFilter: true,
        page,
        limit,
        order: orderString,
        raw: true,
        ...filter,
      }).$promise.then((results) => {
        return results;
        // this.projectAwaitingIntake = results;
      });
    }

