      return this.Project.Intake.query({
        applyFilter: true,
        count: true,
        raw: true,
        ...filter,
      })
        .$promise.then((count) => {
          return count[0];
          // this.projectAwaitingIntake = results;
        })
        .catch((err) => {
          console.error(err);
        });
    }

