    async getRecentProjectsCount() {
      return this.Project.Recent.query({ count: true })
        .$promise.then((count) => {
          return count[0];
        })
        .catch((err) => {
          console.error(err);
        });
    }

    loadWidgets() {
      this.isLoadingData = true;
      return this.Project.Widget.get({ user_id: this.AuthenticatedUser.id })
        .$promise.then((res) => {
          const { charts, tables } = res;
          this.$timeout(() => {
            this.openProjectByDueDate = charts.openProjectByDueDate;
            this.myAsssignmentByDueDate = charts.myAsssignmentByDueDate;

            this.openCommentsByGroup = charts.openCommentByGroup.data;
            this.openCommentsByGroupOptions = charts.openCommentByGroup.options;

            // this.openCommentsByUser = charts.openCommentByUser.data;
            // this.openCommentsByUserOptions = charts.openCommentByUser.options;
          });
          // this.$timeout(() => {
          //     // this.projectAwaitingIntake = tables.projectAwaitingIntake;
          //     // this.projectInCycleComplete = tables.projectInCycleComplete;
          // }, 2000);
        })
        .catch(console.log);
    }

    loadOpenProjectWidget() {
      try {
        this.isLoadingOpenProjectData = true;
        return this.Project.OpenProjectWidget.get().$promise.then((res) => {
          this.openProjectByDueDate = res.openProjectByDueDate;
          this.isLoadingOpenProjectData = false;
        });
      } catch (error) {
        console.log("error: ", error);
      }
    }

    loadMyAssignmentWidget() {
      try {
        this.isLoadingMyAssignmentData = true;
        return this.Project.MyAssignmentWidget.get({
          user_id: this.AuthenticatedUser.id,
        }).$promise.then((res) => {
          this.myAsssignmentByDueDate = res.myAsssignmentByDueDate;
          this.isLoadingMyAssignmentData = false;
        });
      } catch (error) {
        console.log("error: ", error);
      }
    }

    loadOpenCommentByGroupWidget() {
      try {
        this.isLoadingOpenCommentByGroupData = true;
        return this.Project.OpenCommentByGroupWidget.get({}).$promise.then(
          (res) => {
            this.openCommentsByGroup = res.data;
            this.openCommentsByGroupOptions = res.options;
            this.isLoadingOpenCommentByGroupData = false;
          }
        );
      } catch (error) {
        console.log("error: ", error);
      }
    }

    getDefaultDashboardSetting() {
      return [
        {
          id: 1,
          value: true,
          label: "Open Projects by Due Date Graph",
        },
        {
          id: 2,
          value: true,
          label: "My Open Assignments by Due Date Graph",
        },
        {
          id: 3,
          value: true,
          label: "Open Comments by Group Graph",
        },
        {
          id: 4,
          value: true,
          label: "Recently Viewed Projects",
        },
        {
          id: 5,
          value: true,
          label: "Projects Awaiting Intake",
        },
        {
          id: 6,
          value: true,
          label: "Projects in Cycle Complete",
        },
      ];
    }

    setDashboardSetting() {
      this.DashboardSetting.fetch({
        id: this.AuthenticatedUser.id,
      }).$promise.then((data) => {
        if (data && data.data && data.data.dashboard) {
          this.dashboardSetting = JSON.parse(data.data.dashboard);
        } else {
          this.dashboardSetting = this.getDefaultDashboardSetting();
        }
        var i = 1;
        for (let setting of this.dashboardSetting) {
          switch (setting.id) {
            case 1:
              this.is_OpenProjects_visible = setting.value;
              this.openProjectsOrder = "order" + i;
              break;
            case 2:
              this.is_MyAssignments_visible = setting.value;
              this.myAssignmentsOrder = "order" + i;
              break;
            case 3:
              this.is_OpenComments_visible = setting.value;
              this.openCommentsOrder = "order" + i;
              break;
            case 4:
              this.is_RecentlyViewedProjects_visible = setting.value;
              this.recentlyViewProjectsOrder = "order" + i;
              break;
            case 5:
              this.is_ProjectsAwaitingIntake_visible = setting.value;
              this.awaitingIntakeOrder = "order" + i;
              break;
            case 6:
              this.is_ProjectsInCycleComplete_visible = setting.value;
              this.projectsInCycleOrder = "order" + i;
              break;
          }
          i++;
        }
      });
    }

    openSettingDialog() {
      this.DashboardSettingDialog({
        user_id: this.AuthenticatedUser.id,
        settings: this.dashboardSetting,
      })
        .then(() => {
          this.setDashboardSetting();
        })
        .finally(() => {
          setTimeout(() => {
            document.getElementById("dashboardSettingDialogButton").focus();
          }, 0);
        });
    }

    select(row) {
      this.$state.go("projects.project", {
        project_id: row.id,
      });
    }

    goToIntake(row) {
      window.open(`/#!/projects/${row.id}/intake`);
    }

    goToDeliverable(row) {
      window.open(`/#!/projects/${row.id}/deliverables`);
    }

    goToProjectDetail(row) {
      window.open(`/#!/projects/${row.id}/details/info`);
    }
  },
};
export default dashboardComponent;

