import template from "./dashboard.html";
import "./dashboard.styl";
import "./draganddrop.component.css";

import io from "socket.io-client";

const dashboardComponent = {
  template,
  bindings: {},
  controller: /* @ngInject */ class DashboardController {
    static get $inject() {
      return [
        "$log",
        "$state",
        "$stateParams",
        "$timeout",
        "Title",
        "Project",
        "ProjectStatus",
        "ListType",
        "ListItem",
        "Document",
        "Comment",
        "AuthenticatedUser",
        "DocumentAssignment",
        "ProjectDetail",
        "DashboardSettingDialog",
        "DashboardSetting",
      ];
    }
    constructor(
      $log,
      $state,
      $stateParams,
      $timeout,
      Title,
      Project,
      ProjectStatus,
      ListType,
      ListItem,
      Document,
      Comment,
      AuthenticatedUser,
      DocumentAssignment,
      ProjectDetail,
      DashboardSettingDialog,
      DashboardSetting
    ) {
      this.$log = $log;
      this.$state = $state;
      this.$stateParams = $stateParams;
      this.$timeout = $timeout;
      this.Title = Title;
      this.Project = Project;
      this.ProjectStatus = ProjectStatus;
      this.ListType = ListType;
      this.ListItem = ListItem;
      this.Document = Document;
      this.Comment = Comment;
      this.AuthenticatedUser = AuthenticatedUser;
      this.DocumentAssignment = DocumentAssignment;
      this.ProjectDetail = ProjectDetail;
