    loadIntakeProjects(
      limit = 10,
      page = 0,
      order = { id: "order-id", desc: true, key: "id" },
      filter = {},
      viewFilterObject = {}
    ) {
      Object.assign(filter, viewFilterObject);
      if (!order) {
        order = { id: "order-id", desc: true, key: "id" };
      }
      if (!order.key) {
        order.key = "id";
      }
      // const orderId = order['id'].split('-');
      const viewOrder = order.desc ? "DESC" : "ASC";
      const orderString = order.key + " " + viewOrder;

      return this.Project.Intake.query({
        applyFilter: true,
        page,
        limit,
        order: orderString,
        raw: true,
        ...filter,
      }).$promise.then((results) => {
        return results;
        // this.projectAwaitingIntake = results;
      });
    }

    async getIntakeCount(filter) {
      return this.Project.Intake.query({
        applyFilter: true,
        count: true,
        raw: true,
        ...filter,
      })
        .$promise.then((count) => {
          return count[0];
          // this.projectAwaitingIntake = results;
        })
        .catch((err) => {
          console.error(err);
        });
    }

    async getCycleCount(filter) {
      return this.Project.Cycle.query({
        applyFilter: true,
        count: true,
        raw: true,
        ...filter,
      })
        .$promise.then((count) => {
          return count[0];
          // this.projectAwaitingIntake = results;
        })
        .catch((err) => {
          console.error(err);
        });
    }

    loadCycleCompleteProjects(
      limit = 10,
      page = 0,
      order = { id: "order-id", desc: true, key: "id" },
      filter = {},
      viewFilterObject = {}
    ) {
      Object.assign(filter, viewFilterObject);
      if (!order) {
        order = { id: "order-id", desc: true, key: "id" };
      }

      if (!order.key) {
        order.key = "id";
      }
      // const orderId = order['id'].split('-');
      const viewOrder = order.desc ? "DESC" : "ASC";
      const orderString = order.key + " " + viewOrder;
      return this.Project.Cycle.query({
        applyFilter: true,
        page,
        limit,
        order: orderString,
        raw: true,
        ...filter,
      })
        .$promise.then((results) => {
          this.projectInCycleComplete = results;
          return results;
        })
        .catch((err) => {
          console.error(err);
        });
    }

    loadRecentProjects(limit = 10, page = 0) {
      return this.Project.Recent.query()
        .$promise.then((results) => {
          const offset = page * limit || 0;
          return results.slice(offset, offset + limit);
        })
        .catch((err) => {
          console.error(err);
        });
    }

    async getRecentProjectsCount() {
      return this.Project.Recent.query({ count: true })
        .$promise.then((count) => {
          return count[0];
        })
        .catch((err) => {
          console.error(err);
        });
    }

