from axiestudio import CustomComponent
from axiestudio.field_typing import Text


class SaveToFileComponent(CustomComponent):
    display_name = "Save to File"
    description = "Save the input to a file named 'temporary_file_debug.txt'."
    icon = "file-text"

    def build_config(self):
        return {
            "input_value": {"display_name": "Input", "multiline": True},
            "file_path": {
                "display_name": "File Path",
                "value": "temporary_file_debug.txt",
                "advanced": True,
            },
        }

    def build(self, input_value: Text, file_path: Optional[str] = "temporary_file_debug.txt") -> None:
        with open(file_path, "w") as file:
            file.write(input_value)
        self.status = f"Input saved to {file_path}"

