from axiestudio.custom import Component
from axiestudio.io import Output
from langchain.tools import Tool
import ast
import operator

class CalculatorToolComponent(Component):
    display_name = "Calculator Tool"
    description = "A tool that evaluates mathematical expressions."
    icon = ""

    inputs = []  # No inputs for the component itself

    outputs = [
        Output(display_name="Calculator Tool", name="calculator_tool", method="create_tool"),
    ]

    def safe_eval(self, node):
        operators = {
            ast.Add: operator.add,
            ast.Sub: operator.sub,
            ast.Mult: operator.mul,
            ast.Div: operator.truediv,
            ast.Pow: operator.pow,
            ast.USub: operator.neg,
        }

        if isinstance(node, ast.Num):
            return node.n
        elif isinstance(node, ast.BinOp):
            return operators[type(node.op)](self.safe_eval(node.left), self.safe_eval(node.right))
        elif isinstance(node, ast.UnaryOp):
            return operators[type(node.op)](self.safe_eval(node.operand))
        else:
            raise TypeError(f"Unsupported operation: {node}")

    def evaluate_expression(self, expression: str) -> float:
        try:
            tree = ast.parse(expression, mode='eval')
            result = self.safe_eval(tree.body)
            return result
        except (SyntaxError, TypeError, ZeroDivisionError) as e:
            raise ValueError(f"Invalid expression: {str(e)}")

    def create_tool(self) -> Tool:
        def calculator_function(expression: str) -> str:
            try:
                result = self.evaluate_expression(expression)
                return f"The result of '{expression}' is: {result}"
            except ValueError as e:
                return str(e)
            except Exception as e:
                return f"An unexpected error occurred: {str(e)}"

        tool = Tool(
            name="Calculator",
            func=calculator_function,
            description="Evaluates mathematical expressions. Input should be a string containing a valid mathematical expression (e.g., '2+2/3', '(5*3)^2')."
        )

        self.status = "Calculator tool created successfully"
        return tool

