from axiestudio.custom import Component
from axiestudio.io import DataInput, Output
from axiestudio.schema import Data
from axiestudio.schema.message import Message
import json
class VectorRetrieverParserComponent(Component):
    display_name = "Vector Retriever Parser"
    description = "Parse retrieved Documents"
    icon = "custom_components"
    name = "VectorRetrieverParserComponent"

    inputs = [
        DataInput(name="data", display_name="Data", info="The data to convert to text."),
    ]

    outputs = [
        Output(display_name="Text", name="text", method="parsed_data"),
    ]

    def parsed_data(self) -> Message:
        documents = []
        if self.data:
            for document in self.data:
                documents.append(
                    {
                        "metadata": document.metadata,
                        "page_content": document.page_content
                    }
                )
        
            return json.dumps(documents)
        else:
            return None

