import os
import base64
from pydantic.v1 import SecretStr
from axiestudio.custom import Component
from axiestudio.io import MessageTextInput, Output, DataInput
from axiestudio.schema import Data
from axiestudio.schema.message import Message
from axiestudio.inputs import (
    SecretStrInput,
)
from openai import OpenAI

class CustomComponent(Component):
    display_name = "Custom Component"
    description = "Use as a template to create your own component."
    documentation: str = "http://docs.axiestudio.org/components/custom"
    icon = "custom_components"
    name = "CustomComponent"

    inputs = [
        MessageTextInput(name="user_prompt", display_name="User Prompy", value=""),
        MessageTextInput(name="assistante_id", display_name="Openai Assistante ID", value=""),
        DataInput(
            name="image_ids",
            display_name="Openai Image IDs",
            info="Image IDs"
        ),
        SecretStrInput(
            name="openai_api_key",
            display_name="OpenAI API Key",
            info="The OpenAI API Key to use for the OpenAI model.",
            advanced=False,
            value="OPENAI_API_KEY",
        ),
    ]

    outputs = [
        Output(display_name="Output", name="output", method="build_output"),
    ]

    def build_output(self) -> Message:
        openai_api_key = self.openai_api_key
        client = OpenAI(api_key=openai_api_key)

        image_ids = self.image_ids

        thread = client.beta.threads.create()

        content = [
            {
                "type": "text",
                "text": self.user_prompt if self.user_prompt else "empty body"
            }
        ]

        for image_id in image_ids:
            content.append({
                "type": "image_file",
                "image_file": 
                {
                    "file_id": image_id,
                    "detail": "low"
                }
            })

        message = client.beta.threads.messages.create(
            thread_id=thread.id,
            role="user",
            content=content
        )

        run = client.beta.threads.runs.create_and_poll(
            thread_id=thread.id,
            assistant_id=self.assistante_id
        )

        if run.status == 'completed': 
            messages = client.beta.threads.messages.list(
                thread_id=thread.id
            )
            for message in messages:
                return Message(text=message.content[0].text.value)
        else:
            return Message(text=run.status)

