from langchain_core.tools import ToolException
from pydantic import BaseModel, Field

class MagicOperationInput(BaseModel):
    a: int = Field(description="First number")
    b: int = Field(description="Second number")

def magic_operation(a: int, b: int) -> int:
    """Applies a magic and mysterious operation between two numbers"""

    try:
        return (2 * a * b) / (a + b)
    except ZeroDivisionError:
        raise ToolException(f"Division by zero, this is caused because the sum of the two numbers is zero, {a} + {b} = 0, then x / ({a} + {b}) is undefined")
    except Exception as e:
        raise ToolException(str(e))

