from axiestudio.custom import Component
from axiestudio.inputs import FileInput, MessageTextInput
from axiestudio.schema.message import Message
from axiestudio.template import Output
from axiestudio.base.data.utils import TEXT_FILE_TYPES
import csv
from pathlib import Path

class FileCSVComponent(Component):
    display_name = "CSV Question Extractor"
    description = "Load a CSV file with questions and ground truths, and extract based on ID"
    icon = "file-spreadsheet"

    inputs = [
        FileInput( 
            name="csv_file",
            display_name="CSV File",
            file_types=["csv"],
            info="Upload a CSV file with 'id', 'question', and 'ground_truth' columns",
        ),
        MessageTextInput(
            name="id",
            display_name="ID",
            info="Enter the ID to extract the corresponding question and ground truth",
        ),
    ]

    outputs = [
        Output(name="question", display_name="Question", method="extract_question"),
    ]

    def load_csv(self):
        if not self.csv_file:
            raise ValueError("Please upload a CSV file to use this component.")

        resolved_path = self.resolve_path(self.csv_file)
        file_path = Path(resolved_path)

        if file_path.suffix.lower() != '.csv':
            raise ValueError("The uploaded file must be a CSV file.")

        data = {}
        with open(file_path, 'r', newline='', encoding='utf-8') as csvfile:
            csv_reader = csv.DictReader(csvfile)
            for row in csv_reader:
                if 'id' not in row or 'question' not in row:
                    raise ValueError("CSV must contain 'id' and 'question'columns.")
                data[row['id']] = {'question': row['question']}

        return data

    def extract_question(self) -> Message:
        try:
            data = self.load_csv()
            if self.id not in data:
                raise ValueError(f"ID '{self.id}' not found in the CSV file.")
            
            question = data[self.id]['question']
            question_message = Message(text=question)
            self.status = question_message
            return question_message
        except Exception as e:
            error_message = f"An error occurred: {str(e)}"
            self.status = error_message
            return Message(text=error_message)
            
        except Exception as e:
            error_message = f"An error occurred: {str(e)}"
            self.status = error_message
            return Message(text=error_message)

