from axiestudio import CustomComponent
from axiestudio.field_typing import BaseLLM, PromptTemplate, VectorStore, Chain
from langchain.chains import RetrievalQA
from langchain.vectorstores import Weaviate

class WeaviateComponent(CustomComponent):
    documentation: str = "http://docs.axiestudio.org/components/custom"

    def build(self, llm: BaseLLM, prompt: PromptTemplate, vector_store: VectorStore) -> Chain:
        # Ensure the vector_store is an instance of Weaviate
        if not isinstance(vector_store, Weaviate):
            raise ValueError("vector_store must be an instance of Weaviate")

        # Create the retriever with the desired number of documents
        retriever = vector_store.as_retriever(search_kwargs={"k": 10})  # Fetch top 10 relevant documents

        # Create the RetrievalQA chain with the retriever and ensure source documents are returned
        retrieval_qa = RetrievalQA.from_chain_type(
            llm=llm,
            chain_type="stuff",  # You can change this as needed
            retriever=retriever,
            return_source_documents=True
        )

        # Define the chat flow class
        class CustomConversationChain(Chain):
            @property
            def input_keys(self):
                return ["input"]

            @property
            def output_keys(self):
                return ["result", "source_documents"]

            def _call(self, inputs):
                query = inputs["input"]
                result = retrieval_qa({"query": query})
                source_documents = result.get("source_documents", [])

                if not source_documents:
                    return {
                        "result": "No relevant documents found.",
                        "source_documents": []
                    }

                unique_sources = set(doc.metadata.get('file_name', 'Unknown') for doc in source_documents)
                formatted_sources = "\n".join(f"- {source}" for source in unique_sources)
                response = result.get("result", "")

                return {
                    "result": f"{response}\n\nSources:\n{formatted_sources}",
                    "source_documents": source_documents
                }

        return CustomConversationChain()

    def build_config(self):
        return {
            "llm": {"display_name": "LLM"},
            "prompt": {"display_name": "Prompt Template"},
            "vector_store": {"display_name": "Vector Store"},
        }

