class Evaluator(CustomComponent):
    documentation: str = "http://docs.axiestudio.org/components/custom"
    display_name = "Evaluator"
    description = "Run evaluation on batchs of inputs and outputs. Save the scores to Langfuse generation instances. Save the input/output records to Langfuse dataset and experiment if wanted."

    def build_config(self):
        return {
            "batch_inp": {"display_name": "Inputs"},
            "batch_out": {"display_name": "Outputs"},
            "dataset": {"display_name": "Dataset name", "required": False},
            "experiment_name": {"display_name": "Experiment name", "required": False},
            "trace_name": {"display_name": "Trace name", "required": False},
            "field_validators": {"display_name": "Field validators"},
            "scorers": {"display_name": "Scorers"},
            "model_name": {"display_name": "Model name", "required": False},
            "master_key": {"display_name": "Master key", "required": False},
            "tags": {"display_name": "Tags", "required": False}
        }

    def build(self, batch_inp: Any, batch_out: Any, 
                    field_validators: List[Any], 
                    scorers: List[Any], 
                    dataset: Optional[str]=None, 
                    experiment_name: Optional[str]=None, 
                    trace_name: Optional[str]=None, 
                    model_name: Optional[str]=None, 
                    master_key: Optional[str]=None,
                    tags: Optional[List[str]]=None) -> Any:

        if (isinstance(batch_inp,str)):
            if (batch_inp in ('',' ')):
                raise ValueError("Input string must not be empty")
            batch_inp = [batch_inp]
        if (isinstance(batch_out,str)):
            if (batch_out in ('',' ')):
                raise ValueError("Output string must not be empty")
            batch_out = [batch_out]
        if (len(batch_inp)!=len(batch_out)):
            raise ValueError("Inconsistent number of input and output instances encountered")
        if (len(batch_out)==0):
            raise ValueError("Output list must not be empty")
        if (len(batch_inp)==0):
            raise ValueError("Input list must not be empty")

        field_check = {}
        score_list = []
        langfuse = Langfuse(
            secret_key=os.environ.get('LANGFLOW_LANGFUSE_SECRET_KEY', ''),
            public_key=os.environ.get('LANGFLOW_LANGFUSE_PUBLIC_KEY', ''),
            host=os.environ.get('LANGFLOW_LANGFUSE_HOST', ''),
        )

        if (dataset is not None):
            langfuse.create_dataset(name=dataset)

            # Add inputs to Langfuse dataset        
            for inp in batch_inp:
                langfuse.create_dataset_item(
                    dataset_name=dataset,
                    input = inp,
                    expected_output='')

            inputs = langfuse.get_dataset(dataset).items
            for item, out in zip(inputs, batch_out):
                langfuse_generation = langfuse.generation(
                    input=item.input,
                    output=out,
                    model=model_name,
                    name=trace_name
                    ) 
                langfuse.trace(id=langfuse_generation.trace_id, tags=tags)
                if (experiment_name is not None):
                    item.link(langfuse_generation, experiment_name)

                try:
                    out = json.loads(out)
                    if (master_key is not None):
                        out = out[master_key]
                    if (not isinstance(out, list)):
                        out = [out]
                    for out_item in out:
                        # Check if the fields are ready for evaluation
                        for validator in field_validators:
                            value = validator.get_score(out=out_item, inp=item.input)
                            langfuse_generation.score(name=validator.score_name, value=value)
                            score_list.append((validator.score_name, value))
                            field_check[validator.field_name] = value

                        # Calculate scores
                        for scorer in scorers:
                            if (field_check[scorer.field_name]==1):
                                value = scorer.get_score(out=out_item, inp=item.input)
                                langfuse_generation.score(name=scorer.score_name, value=value)
                                score_list.append((scorer.score_name, value))
                except json.JSONDecodeError:
                    langfuse_generation.score(name='requirementExtraction_JSONFormatCompliance', value=0)

        else:
            for inp, out in zip(batch_inp, batch_out):

                langfuse_generation = langfuse.generation(
                    input=inp,
                    output=out,
                    name=trace_name,
                    model=model_name
                    )
                langfuse.trace(id=langfuse_generation.trace_id, tags=tags)
