class RelevancyScorerComponent(CustomComponent):
    documentation: str = "http://docs.axiestudio.org/components/custom"
    description = "Get cosine similarity score (relevancy) between the embedding vectors of two text input"
    display_name = "RelevancyScorer"

    class Scorer:
        def __init__(self, score_name):
            self.score_name = score_name

        def get_score(self, out: Union[str,dict], inp: Optional[str] = None):
            pass

    class RelevancyScorer(Scorer):
        def __init__(self, score_name, field_name, embed_model):
            super().__init__(score_name)
            self.field_name = field_name
            self.embed_model = embed_model

        def cosine_similarity(self, vec1, vec2):
            dot_product = np.dot(vec1, vec2)
            norm1 = np.linalg.norm(vec1)
            norm2 = np.linalg.norm(vec2)
            similarity = dot_product / (norm1 * norm2)
            return similarity

        def get_embedding(self, text: str):
            return self.embed_model.embed_query(text)

        def calculate_relevancy(self, term1: Union[str, list], term2: Union[str, list]):
            if (isinstance(term1, str)):
                term1 = self.get_embedding(term1)
            if (isinstance(term2, str)):
                term2 = self.get_embedding(term2)
            return self.cosine_similarity(term1, term2)

        # get all string values in a user story dict
        def extract_end_strings(self, data): 
            end_strings = []

            if isinstance(data, dict):
                for key, value in data.items():
                    end_strings.extend(self.extract_end_strings(value))

            elif isinstance(data, list):
                for item in data:
                    end_strings.extend(self.extract_end_strings(item))

            elif isinstance(data, str):
                end_strings.append(data)

            return end_strings

        def get_score(self, out: Union[str,dict], inp: Optional[str] = None):
            if (isinstance(out, str)):
                out = json.loads(out)

            if self.field_name=="Summary": # Form the user story summary fields to a whole sentence
                output_content = ["As a " + out[self.field_name]['As a'] + ", I want to " + out[self.field_name]['I want to'] + " so that " + out[self.field_name]['So that']]
            else:
                # A field may have a multiple string values (eg. acceptance criteria, scope items, unknowns, etc.). We extract all of them as a list
                output_content = self.extract_end_strings(out[self.field_name])

            # Get the average relevancy score for all string values.
            scores = [] 
            inp_emb = self.get_embedding(inp)
            for content in output_content:
                scores.append(self.calculate_relevancy(inp_emb, content))
            return np.mean(scores)



    def build_config(self):
        return {
            "embed_model": {"display_name": "Embedding model"},
            "score_name": {"display_name": "Score name"},
            "field_name": {"display_name": "Field name"},
        }

    def build(self, embed_model: OpenAIEmbeddings, score_name: str, field_name: str) -> Any:
        scorer = self.RelevancyScorer(score_name, field_name, embed_model)
        return scorer

